import React, { useState } from "react";
import classnames from "classnames";

const baseClassNames = "w-1/2 py-4 px-1 text-center border-b-2 font-medium text-sm";

const Tab = ({ selected, children }) => {
  const tabsStyle = classnames(baseClassNames, {
    "border-indigo-500 text-indigo-600": selected,
    "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300": !selected,
  })
  console.log(selected, children)

  return (
    <a href="#" className={`border-transparent text-gray-500 hover:text-indigo-300 hover:border-indigo-300 ${baseClassNames}`}>
      {children}
    </a>
  )
}

export const Tabs = () => {
  const selectedTab = useState("Wrap")

  return (
    <div>
      <div className="sm:hidden">
        <label for="tabs" className="sr-only">Select a tab</label>
        <select id="tabs" name="tabs" className="block w-full focus:ring-indigo-500 focus:border-indigo-500 border-gray-300">
          <option>My Account</option>
          <option>Company</option>
          <option selected>Team Members</option>
          <option>Billing</option>
        </select>
      </div>
      <div className="hidden sm:block mb-6 w-full">
        <div className="border-b border-gray-900">
          <nav className="-mb-px flex" aria-label="Tabs">

            {/* <!-- Current: "border-indigo-500 text-indigo-600", Default: "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300" --> */}
            {/* <a href="#" className={`border-indigo-500 text-indigo-600 ${baseClassNames}`} aria-current="page">
              Wrap
            </a> */}
            <Tab selected={true}>
              Wrap
            </Tab>
            <Tab selected={false}>
              Unwrap
            </Tab>
          </nav>
        </div>
      </div>
    </div>
  )
}
