export const Input = ({ name, label, type, placeholder, className }) => (
  <div className={className}>
    <label htmlFor={name} class="block text-md font-medium text-gray-200">{label}</label>
    <div class="mt-1">
      <input 
        type={type} 
        name={name} 
        id={name} 
        className={`shadow-sm bg-gray-700 text-gray-300 p-2 focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-xl text-xl border-gray-300 rounded-md`}
        placeholder={placeholder}
      />
    </div>
  </div>
)
