  <div className={className}>
    <label htmlFor={name} class="block text-md font-medium text-gray-200">{label}</label>
    <div class="mt-1 relative">
      <input 
        type={type} 
        name={name} 
        id={name} 
        className={`shadow-sm bg-gray-700 text-gray-300 px-2 py-3 focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-xl text-xl border-gray-300 rounded-md`}
        placeholder={placeholder}
      />
      <div class="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
        {/* <!-- Heroicon name: solid/question-mark-circle --> */}
