import React, { useState } from "react";
import { Contract } from "@ethersproject/contracts";
import { getDefaultProvider } from "@ethersproject/providers";
 import './index.css';
import { Body, Button, Header, Image, Link, Footer, Stats, Tabs, Input } from "./components";
import spinner from "./spinner.svg";
import arrowRight from "./arrow-right.svg";
import useWeb3Modal from "./hooks/useWeb3Modal";
import { addresses, abis } from "@project/contracts";

async function readOnChainData() {
  // Should replace with the end-user wallet, e.g. Metamask
  const defaultProvider = getDefaultProvider();
  // Create an instance of an ethers.js Contract
  // Read more about ethers.js on https://docs.ethers.io/v5/api/contract/contract/
  const ceaErc20 = new Contract(addresses.ceaErc20, abis.erc20, defaultProvider);
  // A pre-defined address that owns some CEAERC20 tokens
  const tokenBalance = await ceaErc20.balanceOf("0x37Fa7c6B28fc5545d9523D6dbb329Be7e1B9e7A0");
  console.log({ tokenBalance: tokenBalance.toString() });
}

function App() {
  const [provider, loadWeb3Modal, logoutOfWeb3Modal] = useWeb3Modal();
  console.log(provider)
  const [selectedTab, setSelectedTab] = useState("wrap");
  const [isLoadingApproval, setLoadingApproval] = useState(false);
  const [isNXMApproved, setNXMApproved] = useState("false");
  const [isWNXMApproved, setWNXMApproved] = useState("false");

  return (
    <>
      <Header />
      <div class="pt-8 pb-8 bg-gray-700 min-h-screen">
        <div class="max-w-6xl mx-auto px-4 sm:px-6 lg:px-64">
          <Stats />
          <div class="bg-gray-800 overflow-hidden shadow rounded-md w100">
            <Tabs selectedTab={selectedTab} setSelectedTab={setSelectedTab}/>
            <div class="bg-gray-800 px-4 py-5 border-b border-gray-900 sm:px-6">
              <div className="flex flex-nowrap content-between justify-around items-center mb-8 w-80 mx-auto">
                <h3 className="bg-gray-700 px-4 py-2 w-32 text-lg leading-6 font-medium text-gray-200 text-center inline border border-4 border-indigo-600 p-3 rounded-md">
                  { selectedTab === "wrap" ? "NXM" : "wNXM" }
                </h3>
                <Image src={arrowRight} heigh="20" width="20" /> 
                <h3 className="bg-gray-700 px-4 py-2 w-32 text-lg leading-6 font-medium text-gray-200 text-center inline border border-4 border-indigo-600 p-3 rounded-md">
                  { selectedTab === "wrap" ? "wNXM" : "NXM" }
                </h3>
              </div>
              <div className="w-full flex justify-center">
                <Button 
                  variant="primary" 
                  type="submit"
                  size="lg"
                  className="w-11/12 block flex justify-center align-center py-3 mx-auto rounded-md shadow-sm text-2xl text-bold" 
                  onClick={() => setLoadingApproval(true)}
                  disabled={!!provider}
                >
                  {provider ? 'Approve' : 'Connect Wallet'}
                  {isLoadingApproval && <Image src={spinner} width="20" height="20" />}
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default App;
