import React from "react"
import classnames from "classnames"

const baseClassNames =
  "inline-flex items-center justify-center border-transparent rounded shadow-sm focus:outline-none focus:ring-2 focus:ring-offset-2 font-medium border"

const sizeToPadding = (size) => {
  if (size === "xs") return "px-2.5 py-1.5"
  if (size === "sm") return "px-3 py-2"
  if (size === "md") return "px-4 py-2"
  if (size === "lg") return "px-4 py-2"
  else return "px-6 py-3"
}

const variantToColors = (variant) => {
  if (variant === "primary") return "text-white"
  if (variant === "secondary") return "text-indigo-700"
}

export const Button = ({ className, size, variant, onClick, type, disabled = false, ...props }) => {
  const isPrimaryEnabled = !disabled && variant === "primary"
  const isSecondaryEnabled = !disabled && variant === "secondary"
  const isPrimaryDisabled = disabled && variant === "primary"
  console.log({ isPrimaryEnabled, isSecondaryEnabled, text: props.children })

  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled}
      className={classnames(`
          ${sizeToPadding(size)}
          ${variantToColors(variant)}
          text-${size}
          ${baseClassNames}
          ${className}
        `, {
          'cursor-not-allowed': isPrimaryDisabled,
          'bg-gray-600': isPrimaryDisabled,
          'bg-gray-600': isPrimaryDisabled,
          "hover:bg-indigo-700": isPrimaryEnabled,
          'bg-indigo-600': isPrimaryEnabled,
          'bg-gray-200': isSecondaryEnabled,
          'hover:bg-gray-800': isSecondaryEnabled,
          'active:bg-gray-800': isSecondaryEnabled,
        })}
    >
      {props.children}
    </button>
  )
}
