import React from "react";
import { Contract } from "@ethersproject/contracts";
import { getDefaultProvider } from "@ethersproject/providers";
import { useQuery } from "@apollo/react-hooks";

 import './index.css';
import { Body, Button, Header, Image, Link } from "./components";
import logo from "./nexus.svg";
import useWeb3Modal from "./hooks/useWeb3Modal";

import { addresses, abis } from "@project/contracts";
import GET_TRANSFERS from "./graphql/subgraph";

async function readOnChainData() {
  // Should replace with the end-user wallet, e.g. Metamask
  const defaultProvider = getDefaultProvider();
  // Create an instance of an ethers.js Contract
  // Read more about ethers.js on https://docs.ethers.io/v5/api/contract/contract/
  const ceaErc20 = new Contract(addresses.ceaErc20, abis.erc20, defaultProvider);
  // A pre-defined address that owns some CEAERC20 tokens
  const tokenBalance = await ceaErc20.balanceOf("0x37Fa7c6B28fc5545d9523D6dbb329Be7e1B9e7A0");
  console.log({ tokenBalance: tokenBalance.toString() });
}

function WalletButton({ provider, loadWeb3Modal, logoutOfWeb3Modal }) {
  return (
    <Button
      onClick={() => {
        if (!provider) {
          loadWeb3Modal();
        } else {
          logoutOfWeb3Modal();
        }
      }}
    >
      {!provider ? "Connect Wallet" : "Disconnect Wallet"}
    </Button>
  );
}

function App() {
  const { loading, error, data } = useQuery(GET_TRANSFERS);
  const [provider, loadWeb3Modal, logoutOfWeb3Modal] = useWeb3Modal();

  React.useEffect(() => {
    if (!loading && !error && data && data.transfers) {
      console.log({ transfers: data.transfers });
    }
  }, [loading, error, data]);

  return (
    <>
        <Header>
          <WalletButton provider={provider} loadWeb3Modal={loadWeb3Modal} logoutOfWeb3Modal={logoutOfWeb3Modal} />
        </Header>
          {/* <Image src={logo} alt="react-logo" />
          <p>
            Edit <code>packages/react-app/src/App.js</code> and save to reload.
          </p>
          <Button onClick={() => readOnChainData()}>
            Read On-Chain Balance
          </Button>
          <Link href="https://ethereum.org/developers/#getting-started" style={{ marginTop: "8px" }}>
            Learn Ethereum
          </Link>
          <Link href="https://reactjs.org">Learn React</Link>
          <Link href="https://thegraph.com/docs/quick-start">Learn The Graph</Link> */}
          {/* <div className="">
            <div className="bg-white px-4 py-5 border-b border-gray-200 sm:px-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                Job Postings
              </h3>
            </div>
          </div> */}
      <div class="pt-8 max-h-7xl bg-gray-600">
        <div class="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
          <div class="mt-2 grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3"></div>
            <div class="bg-gray-800 overflow-hidden shadow rounded-lg w100">
            <div class="bg-gray-800 px-4 py-5 border-b border-gray-200 sm:px-6">
              <h3 class="text-lg leading-6 font-medium text-gray-900">
                Wrap NXM
              </h3>
            </div>
              <div class="p-5">
                <div class="flex items-center">
                  <div class="flex-shrink-0">
                    {/* <!-- Heroicon name: outline/scale --> */}
                    <svg class="h-6 w-6 text-gray-400" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M3 6l3 1m0 0l-3 9a5.002 5.002 0 006.001 0M6 7l3 9M6 7l6-2m6 2l3-1m-3 1l-3 9a5.002 5.002 0 006.001 0M18 7l3 9m-3-9l-6-2m0-2v2m0 16V5m0 16H9m3 0h3" />
                    </svg>
                  </div>
                  <div class="ml-5 w-0 flex-1">
                    <dl>
                      <dt class="text-sm font-medium text-gray-500 truncate">
                        Account balance
                      </dt>
                      <dd>
                        <div class="text-lg font-medium text-gray-900">
                          $30,659.45
                        </div>
                      </dd>
                    </dl>
                  </div>
                </div>
              </div>
              <div class="bg-gray-50 px-5 py-3">
                <div class="text-sm">
                  <a href="#" class="font-medium text-cyan-700 hover:text-cyan-900">
                    View all
                  </a>
                </div>
              </div>
            </div>
          </div>
          </div>
    </>
  );
}

export default App;
