import React from "react";
import { Contract } from "@ethersproject/contracts";
import { getDefaultProvider } from "@ethersproject/providers";

 import './index.css';
import { Body, Button, Header, Image, Link } from "./components";
import logo from "./nexus.svg";
import useWeb3Modal from "./hooks/useWeb3Modal";

import { addresses, abis } from "@project/contracts";
import GET_TRANSFERS from "./graphql/subgraph";

async function readOnChainData() {
  // Should replace with the end-user wallet, e.g. Metamask
  const defaultProvider = getDefaultProvider();
  // Create an instance of an ethers.js Contract
  // Read more about ethers.js on https://docs.ethers.io/v5/api/contract/contract/
  const ceaErc20 = new Contract(addresses.ceaErc20, abis.erc20, defaultProvider);
  // A pre-defined address that owns some CEAERC20 tokens
  const tokenBalance = await ceaErc20.balanceOf("0x37Fa7c6B28fc5545d9523D6dbb329Be7e1B9e7A0");
  console.log({ tokenBalance: tokenBalance.toString() });
}

function WalletButton({ provider, loadWeb3Modal, logoutOfWeb3Modal }) {
  return (
    <Button
      onClick={() => {
        if (!provider) {
          loadWeb3Modal();
        } else {
          logoutOfWeb3Modal();
        }
      }}
    >
      {!provider ? "Connect Wallet" : "Disconnect Wallet"}
    </Button>
  );
}

function App() {
  const [provider, loadWeb3Modal, logoutOfWeb3Modal] = useWeb3Modal();

  return (
    <>
        <Header>
          <WalletButton provider={provider} loadWeb3Modal={loadWeb3Modal} logoutOfWeb3Modal={logoutOfWeb3Modal} />
        </Header>
      <div class="pt-8 pb-8 bg-gray-700 max-h-screen">
        <div class="max-w-6xl mx-auto px-4 sm:px-6 lg:px-64">
          <div class="mt-2 grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3"></div>
            <div class="bg-gray-800 overflow-hidden shadow rounded-md w100">
              <div>
                <div class="sm:hidden">
                  <label for="tabs" class="sr-only">Select a tab</label>
                  <select id="tabs" name="tabs" class="block w-full focus:ring-indigo-500 focus:border-indigo-500 border-gray-300">
                    <option>My Account</option>
                    <option>Company</option>
                    <option selected>Team Members</option>
                    <option>Billing</option>
                  </select>
                </div>
                <div class="hidden sm:block mb-6 w-full">
                  <div class="border-b border-gray-900">
                    <nav class="-mb-px flex" aria-label="Tabs">
                      <a href="#" class="border-transparent text-gray-500 hover:text-indigo-300 hover:border-indigo-300 w-1/2 py-4 px-1 text-center border-b-2 font-medium text-sm">
                        Unwrap
                      </a>
                      {/* <!-- Current: "border-indigo-500 text-indigo-600", Default: "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300" --> */}
                      <a href="#" class="border-indigo-500 text-indigo-600 w-1/2 py-4 px-1 text-center border-b-2 font-medium text-sm" aria-current="page">
                        Wrap
                      </a>
                    </nav>
                  </div>
                </div>
              </div>
            </div>
            <div class="bg-gray-800 px-4 py-5 border-b border-gray-900 sm:px-6">
              <div>
                <h3 class="text-lg leading-6 pt-3 font-medium text-gray-200 w-full text-center">
                  NXM -> WNXM
                </h3>
              </div>
              <div class="p-5">
                <div class="flex items-center">
                  <div class="flex-shrink-0">
                    {/* <!-- Heroicon name: outline/scale --> */}
                    <svg class="h-6 w-6 text-gray-400" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M3 6l3 1m0 0l-3 9a5.002 5.002 0 006.001 0M6 7l3 9M6 7l6-2m6 2l3-1m-3 1l-3 9a5.002 5.002 0 006.001 0M18 7l3 9m-3-9l-6-2m0-2v2m0 16V5m0 16H9m3 0h3" />
                    </svg>
                  </div>
                  <div class="ml-5 w-0 flex-1">
                    <dl>
                      <dt class="text-sm font-medium text-gray-400 truncate">
                        Account balance
                      </dt>
                      <dd>
                        <div class="text-lg font-medium text-gray-200">
                          $30,659.45
                        </div>
                      </dd>
                    </dl>
                  </div>
                </div>
              </div>
              <div className="w-full">
                <Button variant="primary" type="submit" className="w-10/12 flex justify-center py-2 mx-auto rounded-md shadow-sm text-md">
                  Approve
                </Button>
              </div>
            </div>
          </div>
          </div>
    </>
  );
}

export default App;
